const fs = require('fs');
const path = require('path');

const assetsDir = path.join(__dirname, '../assets');

// Create assets directory if it doesn't exist
if (!fs.existsSync(assetsDir)) {
  fs.mkdirSync(assetsDir, { recursive: true });
}

// Base64 encoded 1x1 pixel images with different colors
const images = {
  'trainer1.jpg': 'data:image/jpeg;base64,',
  'trainer2.jpg': 'data:image/jpeg;base64,',
  'featured_workout.jpg': 'data:image/jpeg;base64,'
};

// Function to convert base64 to file
function base64ToFile(base64String, filename) {
  const base64Data = base64String.replace(/^data:image\/\w+;base64,/, '');
  const buffer = Buffer.from(base64Data, 'base64');
  fs.writeFileSync(path.join(assetsDir, filename), buffer);
}

// Generate all images
Object.entries(images).forEach(([filename, base64]) => {
  base64ToFile(base64, filename);
});

console.log('Placeholder images generated successfully!'); 